const express = require('express')
const app = express()

app.get('/',(rqe,res)=>{
    res.sendFile(__dirname+'/index.html')
})

app.get('/getweather',(req,res)=>{

    fetch('https://api.met.no/weatherapi/locationforecast/2.0/compact?lat=67.2857892&lon=14.5542499',{
        Accept: 'application/json',
        'Content-Type': 'application/json',
        'User-Agent': {sitename:'github.com/isak410'}
    })
    .then(res=>res.json())
    .then(data=>{
        console.log(data)
        res.json(data)
    })

})

app.listen(8080)